import React from "react"
import { MaskedTextField, TextField, FormControl, Radio, DateField, Select } from "bold-ui";

export const TextFieldAdapter = ({input, meta, ...rest}: any) => {
    console.log(input)
    return <TextField {...input} error={meta.error} {...rest} />
}

export const RadioFieldAdapter = ({ input, meta, ...rest }: any) => {
  return <Radio {...input} {...rest} />
}

export const MaskedTextFieldAdapter = ({
    input: { onChange, value },
    label,
    name,
    disabled
  }: any) => {
    return (
      <FormControl error="" htmlFor="birthday-id" label={label} required>
        <MaskedTextField
          id="birthday-id"
          disabled={disabled}
          guide
          mask={[/\d/, /\d/, "/", /\d/, /\d/, "/", /\d/, /\d/, /\d/, /\d/]}
          name={name}
          placeholder="dd/mm/aaaa"
          value={value}
          onChange={onChange}
          placeholderChar='_'
          pipe
          showMask
          keepCharPositions
        />
      </FormControl>
    );
  };

  export const DatePickerAdapter = ({
    input: { onChange, value },
    label,
    name,
    meta
  }: any) => {
    return (
      <FormControl error={meta.error} htmlFor="date-id" label={label} required>
        <DateField
          name={name}
          onChange={onChange}
          value={value}
        />
      </FormControl>
    );
  };

  export const SelectAdapter = ({
    input: { onChange, value },
    label,
    name,
    items,
    itemToString,
    disabled,
    meta
  }: any) => {
    return (
      <FormControl error={meta.error} htmlFor="date-id" label={label} required>
        <Select
          openOnFocus
          name={name}
          onChange={onChange}
          value={value}
          items={items}
          itemToString={itemToString}
          disabled={disabled}
        />
      </FormControl>
    );
  };
