    const {user} = useContext(AuthContext);

    return (
        <HFlow alignItems="center" style={css`position: absolute; left: 10%; margin-top: 1rem`}>
            {user &&
                <Heading style={css`color: #dadce1`} level={2}>
                    Ol√°, {user!.username}
                </Heading>
            }
        </HFlow>
    );
}
