import { css } from "@emotion/core";
import { Alert, Button, Cell, Grid, Heading, HFlow, VFlow } from "bold-ui";
import React from "react"
import { Field, Form, FormRenderProps } from "react-final-form";
import { TextFieldAdapter, MaskedTextFieldAdapter, DatePickerAdapter } from "../../Adapters";

export function AddApointmentView() {

    function handleSubmit(value: any) {
        console.log(value)
    }

    const renderForm = (props: FormRenderProps) => {
        return (
            <VFlow vSpacing={1}>
                <Heading level={1}>
                    Adicionando uma nova consulta
                </Heading>
                <form onSubmit={props.handleSubmit}>
                    <Grid wrap>
                        <Cell xs={6}>
                            <Field
                                component={DatePickerAdapter}
                                name="date"
                                label="Data da consulta"
                                placeholder="Selecione uma data"
                                required
                            />
                        </Cell>
                        <Cell xs={6}>
                            <Field
                                component={MaskedTextFieldAdapter}
                                name="birthdate"
                                guide
                                required
                                label="Data de AniversÃ¡rio" 
                                mask={[/\d/,/\d/,'/',/\d/,/\d/,'/',/\d/,/\d/,/\d/,/\d/]}
                                placeholder="dd/mm/aaaa"
                                disabled={false}
                            />
                        </Cell>
                        <Cell xs={12}>
                            <HFlow justifyContent="flex-start">
                                <Button type="reset" kind="normal" size="small" onClick={props.form.reset}>
                                    Limpar
                                </Button>
                                <Button type="submit" kind="primary" size="small">
                                    Enviar
                                </Button>
                            </HFlow>
                        </Cell>
                    </Grid>
                </form>
            </VFlow>
        )
    }

    return (
        <HFlow style={css`margin: 1rem`} justifyContent="center">
            <Form
                onSubmit={handleSubmit}
                render={renderForm}
            />
        </HFlow>

    );
}
