import React from "react"
import { useQuery } from "@apollo/react-hooks";
import { Heading, HFlow, Icon, VFlow } from "bold-ui";
import { useParams } from "react-router-dom";
import { USER } from "../../graphql/queries";
import { UserResult } from "../../types/User";
import { css } from "@emotion/core";
import { ErrorView } from "../ErrorView";
import Moment from "react-moment";

export function AppointmentView() {

    const { userId } = useParams<{userId: string}>();

    const { loading, data } = useQuery<UserResult>(USER, {
        variables: { id: userId }
    });

    if (loading) {
        return (
            <VFlow>
                <Heading style={css`text-align: center`} color="normal" level={1}>
                    Carregando...
                </Heading>
            </VFlow>
        );
    }

    if (data) {
        console.log(data.user)
        console.log(new Date())
        return (
            <VFlow style={css`margin-top: 1rem`}>
                <Heading style={css`text-align: center`} color="normal" level={1}>
                    Consultas
                </Heading>
                <HFlow alignItems="center">
                    <Icon icon="angleLeft"/>
                    <Heading style={css`text-align: center`} color="normal" level={1}>
                        <Moment format="DD/MM/YYYY" date={new Date()}/>
                    </Heading>
                    <Icon icon="angleRight"/>
                </HFlow>
                <HFlow alignItems="center">

                </HFlow>
            </VFlow>
        )

    } else {
        return (
            <ErrorView/>
        );
    }
}
